sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var vehStatusTracker = current.vehicle_status; //declaring var for the veh status on tracker
    var grInventoryStatus = new GlideRecord('x_1327411_loaner_v_loaner_vehicle_catalog'); //est. connection to the inventory table
    grInventoryStatus.addQuery('sys_id', current.vehicle); //looking up the sys id of the car in the 'vehicle' field of tracker
    grInventoryStatus.query(); //execute query

    if (grInventoryStatus.next()) { //if query finds our match, proceed to the next steps
        if (vehStatusTracker == 'pending_release' || vehStatusTracker == 'out_on_field' || vehStatusTracker == 'returned_for_inspection' || vehStatusTracker == 'sent_for_servicing') { //if vehicle status on Tracker meets any of these criteria, do below: 
            grInventoryStatus.vehicle_status = 'unavailable'; //set 'vehicle_status' on Inventory table to unavailable
        } else if (vehStatusTracker == 'returned_to_warehouse') { //set 'vehicle_status' on Inventory table to available
            grInventoryStatus.vehicle_status = 'available';
        } else if (vehStatusTracker == 'decommissioned') { //set 'vehicle_status' on Inventory table to decommissioned
            grInventoryStatus.vehicle_status = 'decommissioned';

        }
        grInventoryStatus.update(); //update the vehicle_status on inventory table. 
    }

})(current, previous);
