sys_script.script:
(function executeRule(current, previous /*null when async*/) {

var grfindServicingTicket = new GlideRecord ('x_1327411_loaner_v_vehicle_servicing');
grfindServicingTicket.addQuery('vehicle_tracker', current.sys_id); //finding associated Tracker Number of this ticket on the Servicing Ticket
grfindServicingTicket.addQuery('ticket_status', 'open'); //among the previous parameter queried, finding tickets where the ticket status is open.
grfindServicingTicket.query(); //execute this query all together.  

if (!grfindServicingTicket.next()); //after executing the query, if there is no existing ticket, proceed ahead;
const grCreateSerTicket = new GlideRecord('x_1327411_loaner_v_vehicle_servicing'); 
var gdToday = new GlideDate(); //get Today's date

grCreateSerTicket.setValue('vehicle_tracker', current.sys_id); //aligns veh Tracker record with the servicing record through Vehicle Tracker field on Servicing Ticket;
grCreateSerTicket.setValue('vehicle', current.vehicle); //setting value for Vehicle from the current table (Tracker)
grCreateSerTicket.setValue('vehicle_status', 'in_service');  //Set the 'vehicle_status' field to 'in service'
grCreateSerTicket.setValue('ticket_status', 'open'); // set ticket status to 'open';
grCreateSerTicket.setValue('assignment_group', 'e47779fa87ad8e10354abaa6cebb35c5');
grCreateSerTicket.vehicle_issues = current.return_notes;
grCreateSerTicket.insert(); //inserts new ticket with all the parameters above;
})(current, previous);
