sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (newValue == 'returned_for_inspection' || newValue == 'returned_to_warehouse') {
        g_form.setSectionDisplay('return_details', true);
    }
	else {
        g_form.setSectionDisplay('return_details', false);
	}

}
// used in conjunction with the UI Policy: Hide Return Details Section on Load
